// Aggregate and format sheet data for AI processing
const items = $input.all();

const summary = {
  total_rows: items.length,
  columns: Object.keys(items[0].json),
  data: items.map(item => item.json),
  statistics: {
    // Add basic stats if numeric columns exist
  }
};

return [{ json: summary }];

// Convert Markdown to Google Docs format
const markdown = $json.text;

// Simple markdown to structured text conversion
const lines = markdown.split('\n');
const requests = [];

let currentIndex = 1;

lines.forEach(line => {
  if (line.startsWith('# ')) {
    // Heading 1
    const text = line.replace('# ', '');
    requests.push({
      insertText: {
        location: { index: currentIndex },
        text: text + '\n'
      }
    });
    requests.push({
      updateParagraphStyle: {
        range: {
          startIndex: currentIndex,
          endIndex: currentIndex + text.length
        },
        paragraphStyle: {
          namedStyleType: 'HEADING_1'
        },
        fields: 'namedStyleType'
      }
    });
    currentIndex += text.length + 1;
  } else if (line.startsWith('## ')) {
    // Heading 2
    const text = line.replace('## ', '');
    requests.push({
      insertText: {
        location: { index: currentIndex },
        text: text + '\n'
      }
    });
    requests.push({
      updateParagraphStyle: {
        range: {
          startIndex: currentIndex,
          endIndex: currentIndex + text.length
        },
        paragraphStyle: {
          namedStyleType: 'HEADING_2'
        },
        fields: 'namedStyleType'
      }
    });
    currentIndex += text.length + 1;
  } else if (line.trim()) {
    // Regular text
    requests.push({
      insertText: {
        location: { index: currentIndex },
        text: line + '\n'
      }
    });
    currentIndex += line.length + 1;
  }
});

return [{
  json: {
    title: 'AI Generated Report - ' + new Date().toLocaleDateString(),
    content: markdown,
    formattedContent: requests
  }
}];

